script:
frappe.ui.form.on('Sales Order', {
    refresh(frm) {
        // Your code here
    },

    custom_pack_quantity: function(frm) {
        // Calculate custom_box_no when custom_pack_quantity changes
        calculateCustomBoxNo(frm);
    },

    qty: function(frm) {
        // Calculate custom_box_no when qty changes
        calculateCustomBoxNo(frm);
    }
});

function calculateCustomBoxNo(frm) {
    if (frm.doc.custom_pack_quantity && frm.doc.qty) {
        // Perform division and store the result in custom_box_no
        frm.set_value('custom_box_no', frm.doc.qty / frm.doc.custom_pack_quantity);
    } else {
        // Reset custom_box_no if either custom_pack_quantity or qty is not set
        frm.set_value('custom_box_no', null);
    }
}
